    "ingredients": [
        "3 large sweet potatoes (peeled)",
        "1 L cold water",
        "2 tbsp cornstarch",
        "1 tsp garlic powder",
        "1 tsp paprika",
        "1 tsp black pepper",
        "2 tbsp olive oil"
    ],
