    "ingredients": [
        "100 g Chinese spinach (yin choy)",
        "1 salted egg (cooked & diced)",
        "1 century egg (diced)",
        "1 egg (beaten)",
        "4 cloves garlic\u00a0(lightly smashed)",
        "50 g dried ikan bilis (dried anchovies) - rinsed & drained",
        "4 cups water\u00a0",
        "1\u20442 tbsp cooking oil",
        "1 tbsp fried whitebaits/silver fish - for garnish",
        "1 tbsp wolfberries"
    ],
    "instructions": [
        {
            "title": "Cut & clean spinach",
            "contents": [
                "Cut off roots of spinach then peel away some of the thin fibrous layer covering the entire stem.",
                "Cut the spinach into portions approximately 10 cm in length.\u00a0",
                "Rinse to remove all traces of dirt till water runs clear, then drain andset aside."
            ]
        },
        {
            "title": "Boil Soup",
            "contents": [
                "Add ikan bilis to a pot of boiling water.",
                "Heat up \u00bd tbsp oil in a soup pot or wok. Fry garlic till fragrant & add dried ikan bilis broth.",
                "Bring to a boil, add wolfberries & boil for 2-3 mins.",
                "Add spinach, salted egg and century egg.\u00a0",
                "Stir gently and cook for about 2 -3 minutes till the spinach is tender, then add beaten egg.",
                "Season with salt if necessary."
            ]
        }
    ],
