    "ingredients": [
        "5 eggs",
        "100 g white sticky rice (Soaked overnight, water reserved)",
        "1 red chili (finely diced)",
        "1 shallot (chopped)",
        "\u00bc cup shredded coconut",
        "2 tbsp hebi (small dried shrimp)",
        "1 tbsp palm sugar",
        "5 coriander leaves",
        "1 tsp sour cream",
        "vegetable oil",
        "salt",
        "pepper"
    ],
    "instructions": [
        {
            "title": "Cook sticky rice",
            "contents": [
                "In a small pan on medium heat, add 2 tbsp vegetable oil. When hot, add the sticky rice along with the reserved water, spreading out the rice.",
                "Cover for 20 minutes."
            ]
        },
        {
            "title": "Mix egg mixture",
            "contents": [
                "In a large bowl, crack eggs.",
                "Add in chili, shallots, shredded coconut (reserving 1 tsp for garnish), hebi and palm sugar. Whisk well with a whisk or fork."
            ]
        },
        {
            "title": "Cook egg mixture",
            "contents": [
                "Uncover the pan. The rice should now be quite dry.Pour in the egg mixture.\u00a0",
                "Allow it to cook for 1 minute, then flip the egg, and cook for another 1 minute."
            ]
        },
        {
            "title": "Dry fry and pound dried shrimps and desiccated coconut",
            "contents": []
        }
    ],
