        "1 tbsp coriander powder",
        "1 tsp turmeric",
        "5 pcs garlic",
        "2 tbsp lime juice",
        "Seasoning:",
        "1 tbsp seafood stock powder",
        "\u00bc tsp salt",
        "\u00bd tsp ground white pepper",
        "Sambal Ingredients:",
        "50 g curly red chili",
