    "image_url": "https://asianfoodnetwork.com/content/dam/afn/global/en/recipes/ayam-bacem/Ayam_Bacem_article_1920x1280_5.jpg.transform/recipestep-img/img.jpg",
    "ingredients": [
        "1 chicken thigh cut into smaller pieces",
        "700 ml coconut water",
        "150 g palm sugar",
        "50 g tomato sauce",
        "8 g salt",
        "60 g sweet soy sauce",
        "15 g fried onions",
        "Ground spices :",
        "50 g shallots",
        "30 g garlic",
        "15 g candlenut",
        "5 g coriander",
        "3 g cumin",
        "15 g ginger",
        "15 g galangal",
        "Dipping Spices:",
        "2 stalks lemongrass, smashed",
        "3 bay leaves",
        "5 lime leaves"
    ],
    "instructions": [
        {
            "title": "Prepare the spice paste",
            "contents": [
                "Puree the shallots, garlic, candlenut, coriander, cumin, ginger and galangal into a paste."
            ]
        },
        {
            "title": "Prepare the sauce",
            "contents": [
                "Prepare a medium-sized saucepan, then add coconut water, ground spices, lemongrass, bay leaves and lime leaves and bring to a boil.\u00a0\u00a0"
            ]
        },
        {
            "title": "Add seasoning and the chicken",
            "contents": [
                "Once boiling, add palm sugar, tomato sauce, salt and sweet soy sauce.",
                "Stir continuously till the sugar completely dissolves.",
                "Then add the chicken.",
                "You many use other parts of the chicken according to your preference. ",
                "If you like it spicy, add chilies in small quantities."
            ]
        },
        {
            "title": "Cook until the sauce is reduced",
            "contents": [
                "Cook the chicken on medium heat until it boils.",
                "Then reduce to a low heat and continue cooking for 1 hour until the sauce reduces and thickens.",
                "After the chicken is cooked, set aside."
            ]
        }
    ],
