    "instructions": [
        {
            "title": "Heat up pancakes, cook sausage patty and egg",
            "contents": [
                "Pan fry frozen chicken sausage patty according to directions on packaging,\u00a0 ",
                "or make your own",
                "In a round cooking mould, cook 1 egg",
                "Heat up pancakes in microwave."
            ]
        },
        {
            "title": "Assemble burger",
            "contents": [
                "Remove pancakes and place one piece on a plate.",
                "Drizzle some maple syrup on the pancake.",
                "Add a slice of cheese and chicken sausage patty.",
                "Top with egg and drizzle 2 tbsp maple syrup.",
                "Top off with another slice of pancake."
            ]
        }
    ],
